var isActive;

if (msg.status === "level1") {
    startTime = Date.now();
    // Enregistrer la nouvelle valeur de startTime dans le contexte global
    global.set('postes.5.startTime', startTime);
    isActive = true;
    // Définir un intervalle pour mettre à jour le temps écoulé toutes les secondes
    if (isActive) {
        var intervalId = setInterval(function() {
            var currentTime = Date.now();
            var elapsedTime = currentTime - startTime;

            var days = Math.floor(elapsedTime / 86400000);
            var hours = Math.floor((elapsedTime % 86400000) / 3600000);
            var minutes = Math.floor((elapsedTime % 3600000) / 60000);
            var seconds = Math.floor((elapsedTime % 60000) / 1000);

            // Formater le temps écoulé en fonction de s'il est supérieur à 24 heures
            if (days > 0) {
                formattedTime = ('0' + days).slice(-2) + 'd ' +
                    ('0' + hours).slice(-2) + 'h ' +
                    ('0' + minutes).slice(-2) + 'm';
            } else {
                formattedTime = ('0' + hours).slice(-2) + 'h ' +
                    ('0' + minutes).slice(-2) + 'm ' +
                    ('0' + seconds).slice(-2) + 's';
            }

            node.send({timer: formattedTime});
            global.set('postes.5.timer', formattedTime)
            // Envoyer le temps écoulé au nœud Debug

        }, 1000); // Mettre à jour toutes les secondes
    }

    // Enregistrer l'ID de l'intervalle dans le contexte global
    global.set('postes.5.intervalId10', intervalId);
} else if (msg.status === "off") {
    isActive = false;
    formattedTime = "00h 00m 00s"
    global.set('postes.5.timer', formattedTime)
    node.send({timer: formattedTime});
    // Supprimer l'intervalle lors de l'arrêt
    if (global.get('postes.5.intervalId10')) {
        clearInterval(global.get('postes.5.intervalId10'));
        global.set('postes.5.intervalId10', null);
    }
}

return msg;

